game.script:
init() {
        this.initEffectsAndUpgrades();

}
update() {
   this.game.state.stats = {...this.game.state.defaultStats};
}
postUpdate() {

        if (this.game.state.gameOver || this.game.state.victory || this.game.state.isLevelingUp) return;

        // Game over check
        if (this.game.state.bloodCoreHP <= 0 && !this.game.state.gameOver) {
            this.gameOver();
        }
}


    // Game-over and victory functions
    gameOver() {
        this.game.state.gameOver = true;
        this.game.state.isPaused = true;
        gameOverWave.textContent = this.state.round + 1;
        gameOverMenu.style.display = 'block';
        overlay.style.display = 'block';
    }

    gameVictory() {
        this.game.state.victory = true;
        this.game.state.isPaused = true;
        victoryMenu.style.display = 'block';
        overlay.style.display = 'block';
    }


    // Tower placement system


    initEffectsAndUpgrades() {

const Upgrade = class { 
    constructor(id, title, desc, icon, appliesTo, condition, apply, onAcquire) {
        this.id = id;
        this.title = title;
        this.desc = desc;
        this.icon = icon;
        this.appliesTo = appliesTo;
        this.conditionFn = condition;
        this.applyFn = apply;
        this.onAcquire = onAcquire;        
    }

    canApply(gameState) {
        return this.conditionFn(gameState);
    }

    apply(s, add, mul) {
        this.applyFn(s, add, mul);
    }
}


        this.game.effects = {
            slow: (stats, additiveStats, multiplicitiveStats, slowAmount) => {
                stats[this.game.config.effects.slow.stat] *= slowAmount;
            }
        }
        this.game.upgrades = [
            // Bat Swarm Upgrades
            new Upgrade(
                'sentryFrenzy',
                'Sentry Frenzy',
                'Sentry Swarm: ' + this.game.config.upgrades.sentryFrenzy.desc,
                'ðŸ¦‡',
                'sentry',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {
                    multiplicitiveStats['attackSpeed'].push(this.game.config.upgrades.sentryFrenzy.value);
                }
            ),
            new Upgrade(
                'sentryIntelligence',
                'Sentry Intelligence',
                'Sentry Swarm: ' + this.game.config.upgrades.sentryIntelligence.desc,
                'ðŸ¦‡',
                'sentry',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {            
                    multiplicitiveStats['damage'].push(this.game.config.upgrades.sentryIntelligence.damage);
                    multiplicitiveStats['range'].push(this.game.config.upgrades.sentryIntelligence.range);        
                }
            ),

            // Necromancer Upgrades
            new Upgrade(
                'necroSummon',
                'Raise Dead',
                'Necromancer: ' + this.game.config.upgrades.necroSummon.desc,
                'ðŸ’€',
                'fabricator',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {
                    stats.summonChance = 1;
                    if(!additiveStats.summonChance) additiveStats.summonChance = [];
                    additiveStats['summonChance'].push(this.game.config.upgrades.necroSummon.summonChance);
                }
            ),

            // Shadow Turret Upgrades
            new Upgrade(
                'overCharge',
                'Overcharge',
                'Tesla Coil: ' + this.game.config.upgrades.overCharge.desc,
                'ðŸ“',
                'teslaCoil',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {
                    additiveStats['range'].push(this.game.config.upgrades.overCharge.range);
                }
            ),

            // Soul Pyre Upgrades
            new Upgrade(
                'pyreSoul',
                'Radiant Soul',
                'Soul Pyre: ' + this.game.config.upgrades.pyreSoul.desc,
                'ðŸ’‰',
                'soulPyre',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {
                    additiveStats['splashRadius'].push(this.game.config.upgrades.pyreSoul.splashRadius);
                }
            ),

            // Mist Shrine Upgrades
            new Upgrade(
                'mistSlow',
                'Chilling Mist',
                'Mist Shrine: ' + this.game.config.upgrades.mistSlow.desc,
                'â„ï¸',
                'mistShrine',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {
                    multiplicitiveStats['slowEffect'].push(this.game.config.upgrades.mistSlow.slowEffect);
                }
            ),
            // Global Upgrades
            new Upgrade(
                'homeReinforcement',
                'Reinforcement',
                this.game.config.upgrades.bloodCore.desc,
                'ðŸ›¡ï¸',
                'global',
                (state) => true,
                (stats) => {
                    stats.maxBloodCoreHP *= this.game.config.upgrades.bloodCore.maxHpMultiplier;
                },
                (state) => {
                    state.bloodCoreHP = Math.min(state.stats.maxBloodCoreHP, state.bloodCoreHP + this.game.config.upgrades.bloodCore.healAmount);
                }
            ),
            new Upgrade(
                'essenceExtraction',
                'Essence Extraction',
                this.game.config.upgrades.essenceExtraction.desc,
                'ðŸ”®',
                'global',
                (state) => true,
                (stats) => {
                    stats.essenceMultiplier *= this.game.config.upgrades.essenceExtraction.value;
                }
            ),
            new Upgrade(
                'essenceOverflow',
                'Essence Overflow',
                this.game.config.upgrades.essenceOverflow.desc,
                'ðŸ”®',
                'global',
                (state) => state.bloodCoreHP > state.stats.maxBloodCoreHP / 2,
                (stats) => {
                    stats.essenceMultiplier *= this.game.config.upgrades.essenceOverflow.value;
                }
            ),

        ];

    }

waveManager.script:
init() {   

        this.resetWaveState();
    }

    resetWaveState() {
        this.game.state.waveSets = this.game.config.levels[this.game.state.level].wavesets;
        this.game.state.currentWaveIds = [];
        this.game.state.currentWaveEnemies = [];
        this.game.state.enemiesSpawned = [];
        this.game.state.spawnTimers = []; // Array for individual spawn timers
        this.game.state.spawnRate = 1;    // Base spawn rate (can be modified per waveset if needed)
        this.game.state.waveTimer = 0;
        this.game.state.startDelayTimer = 0;
        this.game.state.round = 0;
    }

    update() {
        // Skip wave updates if game is over or in certain states
        if (this.game.state.gameOver || this.game.state.victory || this.game.state.isLevelingUp) return;

        this.game.state.startDelayTimer += this.game.deltaTime;

        // Process all wavesets in parallel
        for (let i = 0; i < this.game.state.currentWaveIds.length; i++) {
            let waveSet = this.game.config.wavesets[this.game.state.waveSets[i]];

            // Skip if still in start delay
            if (waveSet.startDelay && this.game.state.startDelayTimer < waveSet.startDelay) continue;

            // Update individual spawn timer for this waveset
            this.game.state.spawnTimers[i] += this.game.deltaTime;

            // If this waveset still has enemies to spawn
            if (this.game.state.enemiesSpawned[i] < this.game.state.currentWaveEnemies[i].length) {
                if (this.game.state.spawnTimers[i] >= this.game.state.spawnRate) {
                    // Create enemy from the appropriate waveset using the enemy type and start point index
                    const enemyType = this.game.state.currentWaveEnemies[i][this.game.state.enemiesSpawned[i]];
                    const startPointIndex = i;


        this.game.addEntity(this.game.createEntityFromConfig(0, 0, 'enemy', { objectType: "enemies", spawnType: enemyType, pathIndex: startPointIndex}));
                    this.game.state.enemiesSpawned[i]++;
                    this.game.state.spawnTimers[i] = 0; // Reset this waveset's timer

                    // Calculate total progress across all wavesets
                    const totalEnemies = this.game.state.currentWaveEnemies.reduce((sum, wave) => sum + wave.length, 0);
                    const totalSpawned = this.game.state.enemiesSpawned.reduce((sum, count) => sum + count, 0);

                    // Update wave progress bar
                    document.getElementById('waveProgress').style.width = (totalSpawned / totalEnemies * 100) + '%';
                }
            }
        }

        // Check if all wavesets have completed spawning
        const allWavesetsComplete = this.game.state.enemiesSpawned.every((spawned, index) => 
            spawned >= this.game.state.currentWaveEnemies[index].length
        );

        // Move to next wave if all enemies defeated and all wavesets have completed spawning
        if (this.game.state.enemies.length === 0 && allWavesetsComplete) {
            this.game.state.waveTimer++;

            if (this.game.state.waveTimer >= this.game.state.waveDelay) {
                this.startNextWave();
            }
        }
    }

    startNextWave() {
        document.getElementById('waveDisplay').textContent = this.game.state.round + 1;

        this.game.state.currentWaveIds = [];
        this.game.state.currentWaveEnemies = [];
        this.game.state.enemiesSpawned = [];
        this.game.state.spawnTimers = [];

        let totalWaves = 0;
        for (let i = 0; i < this.game.state.waveSets.length; i++) {
            const waveSetId = this.game.state.waveSets[i];
            const waveSet = this.game.config.wavesets[waveSetId];

            // Check if this waveset has enough waves for the current round
            if (this.game.state.round < waveSet.waves.length) {
                const currentWaveId = waveSet.waves[this.game.state.round];

                // Add this wave to the current active waves
                this.game.state.currentWaveIds.push(currentWaveId);
                this.game.state.currentWaveEnemies.push(this.game.config.waves[currentWaveId].enemies);
                this.game.state.enemiesSpawned.push(0);
                this.game.state.spawnTimers.push(0); // Initialize spawn timer for this waveset
            }

            totalWaves = Math.max(totalWaves, waveSet.waves.length);
        }

        // If all wavesets are exhausted, end the game
        if (this.game.state.currentWaveIds.length === 0) {
            this.game.state.victory = true;
            this.game.state.isPaused = true;
            document.getElementById('victoryMenu').style.display = 'block';
            document.getElementById('overlay').style.display = 'block';
            return;
        }

        this.game.state.maxWaves = totalWaves;
        this.game.state.spawnRate = 5;
        this.game.state.waveTimer = 0;
        this.game.state.startDelayTimer = 0;

        // Reset wave progress bar
        document.getElementById('waveProgress').style.width = '0%';
        this.game.state.round++;
    }

upgradeManager.script:
 init() {
this.game.state.activeUpgrades = {};
}
    update() {
       this.applyActiveUpgrades();
        // Level Up check
        if (this.game.state.essence >= this.game.state.essenceToNextLevel && !this.game.state.isLevelingUp) {
            this.showUpgradeMenu();
        }
    }

    applyActiveUpgrades() {
        engine.getFunction("calculateStats")(this.game.state.stats, this.game.state.activeUpgrades['global']);    
    }


    // Upgrade system
    showUpgradeMenu() {
        if (this.game.state.isLevelingUp) return; // Prevent re-triggering

        this.game.state.isLevelingUp = true;
        this.game.state.isPaused = true;

        this.game.uiManager.upgradeMenu.style.display = 'block';
        this.game.uiManager.overlay.style.display = 'block';
        this.game.uiManager.upgradeOptionsDiv.innerHTML = '';

        // Filter upgrades based on conditions
        const availableUpgrades = this.game.upgrades.filter(upgrade => upgrade.canApply(this.game.state));

        // Choose 3 random upgrades
        const options = [];
        while (options.length < 3 && availableUpgrades.length > 0) {
            const index = Math.floor(Math.random() * availableUpgrades.length);
            options.push(availableUpgrades[index]);
            availableUpgrades.splice(index, 1);
        }

        // Create upgrade options
        options.forEach(upgrade => {
            const div = document.createElement('div');
            div.className = 'upgrade-option';
            div.innerHTML = `
                <div class="upgrade-icon">${upgrade.icon}</div>
                <div class="upgrade-desc">
                    <div class="upgrade-title">${upgrade.title}</div>
                    ${upgrade.desc}
                </div>
            `;
            div.onclick = () => this.selectUpgrade(upgrade);
            this.game.uiManager.upgradeOptionsDiv.appendChild(div);
        });
    }

    selectUpgrade(upgrade) {       
        // Add to active upgrades list if not already
        if (!this.game.state.activeUpgrades[upgrade.appliesTo]) {
            this.game.state.activeUpgrades[upgrade.appliesTo] = [upgrade];
        } else {
            this.game.state.activeUpgrades[upgrade.appliesTo].push(upgrade);
        }

        this.applyActiveUpgrades();
        if(upgrade.onAcquire) {
            upgrade.onAcquire(this.game.state);
        }

        upgradeMenu.style.display = 'none';
        overlay.style.display = 'none';

        this.game.state.essence -= this.game.state.essenceToNextLevel;
        this.game.state.level++;
        this.game.state.essenceToNextLevel = Math.floor(this.game.state.essenceToNextLevel * 1.4);        

        this.game.state.isLevelingUp = false;
        this.game.state.isPaused = false;
    }

stats.script:
init( {objectType, spawnType} ) {        

      let stats = this.game.config[objectType][spawnType];
      this.type = spawnType;
      this.stats = {...stats};
      this.defaultStats = {...this.stats};
      this.activeEffects = {};
  }
  update() {
      this.stats = {...this.defaultStats};
      this.applyEffects();
      this.applyUpgrades();
  }
  addStat(statName, statValue) {
      this.stats[statName] = statValue;
      this.defaultStats[statName] = statValue;
  }
  addEffect(effectConfig, effectFn, effectAmt) {        
      this.activeEffects[effectConfig.id] = this.parent.addComponent(engine.getComponent("effect"), { config: effectConfig, applyFn: effectFn, amount: effectAmt });
  }
  applyEffects() {
      let effectArr = [];
      for(let effectId in this.activeEffects) {
          if(this.activeEffects[effectId] && this.activeEffects[effectId].lifeTime > 0){
              effectArr.push(this.activeEffects[effectId]);
          } else {
              this.activeEffects[effectId] = undefined;
          }
      }        

      engine.getFunction("calculateStats")(this.stats, effectArr);
  }

  applyUpgrades() {
      engine.getFunction("calculateStats")(this.stats, this.game.state.activeUpgrades[this.type]);        
  }

animator.script:
init({ objectType, spawnType}) {
    this.animations = this.game.imageManager.getImages(objectType, spawnType); // { "idle": [...], "walk": [...] }
    this.currentAnimation = "idle";
    if(this.animations.walk) this.currentAnimation = "walk";
    this.currentFrame = 0;
    this.frameDuration = 10; // 10 frames per animation frame (~0.166s at 60 FPS)
    this.frameTimer = 0;

    this.baseSpeed = this.parent.getComponent("stats").stats.speed || 1;
}

update() {
    this.frameTimer++;
    let currentSpeedPercent = this.parent.getComponent("stats").stats.speed / this.baseSpeed || 1;
    if (this.frameTimer >= this.frameDuration / currentSpeedPercent) {
        this.frameTimer = 0;
        const animFrames = this.animations[this.currentAnimation];
        this.currentFrame = (this.currentFrame + 1) % animFrames.length;
    }

    // Sync direction with Renderer (if separate)
    const renderer = this.parent.getComponent("Renderer");
    if (renderer) {
        renderer.images = this.animations[this.currentAnimation][this.currentFrame];
    }
}

setAnimation(animationType) {
    if (this.animations[animationType] && this.currentAnimation !== animationType) {
        this.currentAnimation = animationType;
        this.currentFrame = 0;
        this.frameTimer = 0;
    }
}

essenceBounty.script:
init(){
    this.statsComp = this.parent.getComponent('stats');
}
destroy() {               
    this.game.state.essence += this.statsComp.stats.essence * this.game.state.stats.essenceMultiplier;        
}

followPath.script:
init( { pathIndex = 0 }) {
    this.gridSize = this.game.config.configs.game.gridSize;
    this.pathIndex = pathIndex;
    this.indexInPath = 0;
    this.x = this.game.state.paths[this.pathIndex][this.indexInPath].x;
    this.y = this.game.state.paths[this.pathIndex][this.indexInPath].y;
    this.parent.position = { x: this.x * this.gridSize + this.gridSize / 2, y: this.y * this.gridSize + this.gridSize / 2 };        
}

update() {
    this.stats = this.getComponent('stats').stats;
    this.gridSize = this.game.config.configs.game.gridSize;
    if (this.indexInPath < this.game.state.paths[this.pathIndex].length - 1) {
        const target = this.game.state.paths[this.pathIndex][this.indexInPath + 1];
        const dx = target.x * this.gridSize + (this.gridSize / 2) - this.parent.position.x;
        const dy = target.y * this.gridSize + (this.gridSize / 2) - this.parent.position.y;
        const dist = Math.hypot(dx, dy);

        if (dist > this.stats.speed) {
            this.parent.position.x += (dx / dist) * this.stats.speed;
            this.parent.position.y += (dy / dist) * this.stats.speed;
        } else {
            this.indexInPath++;
        }
        this.x = parseInt(this.parent.position.x / this.gridSize);
        this.y = parseInt(this.parent.position.y / this.gridSize);
    } else {
        this.game.state.bloodCoreHP -= this.stats.value;
        this.parent.destroy();
        return false;
    }
}

arrayTracker.script:
init( {objectType}) {
    this.arr = objectType;
    if(!this.game.state[this.arr]){
        this.game.state[this.arr] = [];
    }
    this.game.state[this.arr].push(this.parent);
}

destroy(){
    let index = this.game.state[this.arr].indexOf(this.parent);
    this.game.state[this.arr].splice(index, 1);        
}

populationBurden.script:
init(){
    this.stats = this.parent.getComponent('stats').stats;        
}
update() {
    if(this.stats){ 
        if( this.stats.population ) {
            this.game.state.stats.population += this.stats.population;
        } 
        if( this.stats.supply ) {
            this.game.state.stats.maxPopulation += this.stats.supply;
        }
    }
}

attacker.script:
 init() {        
        this.stats = this.getComponent('stats').stats;    
        this.level = 1;
        this.target = null;
        this.projectiles = [];  
        this.cooldown = 0;
    }

    update() {
        if (this.cooldown > 0) this.cooldown--;

        // Validate current target
        if (this.target) {
            const distance = Math.hypot(
                this.target.position.x - this.parent.position.x,
                this.target.position.y - this.parent.position.y
            );
            if (distance > this.stats.range || this.target.getComponent('health').hp <= 0) {
                this.target = null;
            }
        }

        // Find target if none
        if (!this.target) {
            this.findTarget();
        }

        // Attack if ready and has target
        if (this.cooldown <= 0 && this.target) {
            this.attack();
        }

        if (this.cooldown <= 0 && this.stats.mineAmt > 0) {
            this.gather();
        }
        return true;
    }

    findTarget() {
        this.target = null;
        let furthestEnemy = null;
        let furthestDistance = -1;
        const nearbyEntities = this.game.spatialGrid.getNearbyEntities(
            this.parent.gridPosition.x, 
            this.parent.gridPosition.y, 
            this.stats.range
        );
        for (let enemy of nearbyEntities) {
            let enemyHP = enemy.getComponent("health").hp;
            let followPath = enemy.getComponent('followPath');
            if (enemyHP <= 0) continue;          

            let distanceToEnd = this.game.state.paths[followPath.pathIndex].length - followPath.indexInPath;
            if (distanceToEnd > furthestDistance) {
                furthestDistance = followPath.indexInPath;
                furthestEnemy = enemy;
            }  
        }

        this.target = furthestEnemy;
    }

    gather() {
        this.game.state.bloodShards += this.stats.mineAmt;
        this.cooldown = this.stats.attackSpeed;
    }

    attack() {
        if (!this.target) return; 
        this.launchProjectile();
        this.cooldown = this.stats.attackSpeed;
    }

    launchProjectile() {
        this.stats = this.getComponent('stats').stats;    
        let projectileType = this.stats.projectile;
        let projectileDef = this.game.config.projectiles[projectileType];
        let projStats = { ...projectileDef };
        delete projStats.render;
        projStats.baseDamage = this.stats.damage || 1; 
        projStats.speed = this.stats.speed || 5;     
        projStats.piercing = this.stats.piercing || 0;
        projStats.splashRadius = this.stats.splashRadius || 0;
        projStats.critChance = this.stats.critChance || 0.05;
        projStats.critMultiplier = this.stats.critMultiplier || 2;

 if(projectileDef.customRenderer == "lightning") {
 this.game.spawn(this.parent.position.x, this.parent.position.y, 'lightningProjectile', { objectType: "projectiles", spawnType: projectileType, target: this.target, owner: this.parent, stats: projStats });
} else {
 this.game.spawn(this.parent.position.x, this.parent.position.y, 'projectile', { objectType: "projectiles", spawnType: projectileType, target: this.target, owner: this.parent, stats: projStats });
}
    }

projectile.script:
   init( {spawnType, owner, target, stats}) {
       this.type = spawnType;
       this.def = this.game.config.projectiles[this.type];
       this.owner = owner;
       this.target = target;
       this.stats = stats;
       this.piercedEnemies = [];
       this.ownerStats = this.owner.getComponent("stats").stats;
       this.distanceTraveled = 0;
       this.distanceToSpawnParticle = 24;
   }

   update() {
       // Remove if target is gone
       if (!this.target || this.target.destroyed) {
           this.parent.destroy();
       }
       // Move towards target
       const dx = this.target.position.x - this.parent.position.x;
       const dy = this.target.position.y - this.parent.position.y;
       const distSq = dx * dx + dy * dy;

       // Hit detection
       if (distSq < 15 * 15 ) {
           let targetHealth = this.target.getComponent("health");
           let targetEnergyShield = this.target.getComponent("energyshield");
           let targetStats = this.target.getComponent("stats");
           let targetStatClone = {...targetStats.stats};
           targetStatClone.energyShield = targetEnergyShield.energyShield;
           if (this.stats.splashRadius > 0) {
               const nearbyEnemies = this.game.spatialGrid.getNearbyEntities(
                   this.parent.gridPosition.x, 
                   this.parent.gridPosition.y, 
                   this.stats.splashRadius
               );
               // Process only relevant enemies
               for (const enemy of nearbyEnemies) {
                   if (enemy.isDead) continue;
                   let enemyHealth = enemy.getComponent("health");
                   let enemyEnergyShield = this.target.getComponent("energyshield");
                   let enemyStats = enemy.getComponent("stats");
                   let enemyStatClone = {...enemyStats.stats};
                   enemyStatClone.energyShield = enemyEnergyShield.energyShield;
                   const dx = enemy.position.x - this.target.position.x;
                   const dy = enemy.position.y - this.target.position.y;
                   const distSq1 = dx * dx + dy * dy;

                   let gridSize = this.game.config.configs.game.gridSize;
                   // Compare with squared splash radius for efficiency
                   const splashRadiusSq = this.stats.splashRadius * this.stats.splashRadius * gridSize * gridSize;
                   if (distSq1 <= splashRadiusSq) {
                       // Calculate actual distance only when needed
                       //const splashDist = Math.sqrt(distSq1);
                       let damageResult = engine.getFunction("calculateDamage")(this.stats, enemyStatClone);                    
                       if( !damageResult.wasEvaded ) {                        
                           enemyHealth.hp -= damageResult.damageDealt;
                           enemyEnergyShield.absorbDamage(damageResult.damageAbsorbed);
                           this.game.spawn(enemy.position.x, enemy.position.y, "hitEffect", { damageType: this.stats.damageType});
                           if( this.ownerStats.slowEffect ) {
                               enemyStats.addEffect(this.game.config.effects.slow, this.game.effects.slow, this.ownerStats.slowEffect);
                           }
                       }
                   }
               }
               let explosion = this.game.spawn(this.parent.position.x, this.parent.position.y, "explosion", { radius: this.stats.splashRadius });            
           } else {
               // Apply damage
               let damageResult = engine.getFunction("calculateDamage")(this.stats, targetStatClone);                    
               if( !damageResult.wasEvaded ) {  
                   targetHealth.hp -= damageResult.damageDealt;
                   targetEnergyShield.absorbDamage(damageResult.damageAbsorbed);
                   this.game.spawn(this.target.position.x, this.target.position.y, "hitEffect", { damageType: this.stats.damageType});
                   if( this.ownerStats.slowEffect ) {
                       targetStats.addEffect(this.game.config.effects.slow, this.game.effects.slow, this.ownerStats.slowEffect );
                   }
               }
           }

           //summon skeleton
           if (this.ownerStats.summonChance > 0 && 
               targetHealth.hp <= 0 && 
               Math.random() < this.ownerStats.summonChance - 1) {
this.game.spawn(this.target.position.x, this.target.position.y, 'summonedTower', { objectType: "towers", spawnType: this.ownerStats.summonType, owner: this.owner  });               
           }

           // Apply tower special effects
           if (this.ownerStats.leech > 0) {
               const healing = this.stats.damage * this.ownerStats.leech * this.game.state.stats.healingMultiplier;
               this.game.state.bloodCoreHP = Math.min(this.game.state.stats.maxBloodCoreHP, this.game.state.bloodCoreHP + healing);
           }
           if (this.ownerStats.thief && this.ownerStats.thief != 0) {
               const stealAmt = this.stats.damage * this.ownerStats.thief * this.game.state.stats.bloodShardMultiplier;
               this.game.state.bloodShards += stealAmt;
           }
           // Piercing logic
           if (this.stats.piercing > 0 && this.piercedEnemies.length < this.stats.piercing) {
               this.piercedEnemies.push(this.target);
               const nearbyEnemies = this.game.spatialGrid.getNearbyEntities(
                   this.parent.gridPosition.x, 
                   this.parent.gridPosition.y, 
                   this.ownerStats.range
               );
               // Find a new target
               let newTarget = null;
               for (let enemy of nearbyEnemies) {
                   if (!enemy.destroyed && !this.piercedEnemies.includes(enemy)) {
                       const dx = enemy.position.x - this.parent.position.x;
                       const dy = enemy.position.y - this.parent.position.y;
                       const distSq2 = dx * dx + dy * dy;
                       const gridSize = this.game.config.configs.game.gridSize;
                       if (distSq2 < this.ownerStats.range * this.ownerStats.range * gridSize * gridSize) {
                           newTarget = enemy;
                           break;
                       }
                   }
               }

               if (newTarget) {
                   this.target = newTarget;   
                   return;                 
               } 
           } 

           this.parent.destroy();
       }



       // Move projectile
       let dist = Math.sqrt(distSq);
       const speed = this.stats.speed;
       this.parent.position.x += (dx / dist) * speed;
       this.parent.position.y += (dy / dist) * speed;

       const tDx = this.parent.lastPosition.x - this.parent.position.x;
       const tDy = this.parent.lastPosition.y - this.parent.position.y;
       const tdistSq = tDx * tDx + tDy * tDy;
       let tDist = Math.sqrt(tdistSq);


       this.distanceTraveled += tDist;
       if( this.def.particle && this.distanceTraveled > this.distanceToSpawnParticle ) {
           this.game.createParticle( this.def.particle, this.parent.position.x, this.parent.position.y);
           this.distanceTraveled = 0;
           this.distanceToSpawnParticle += Math.random()*3;
       }
   }

hitEffectParticle.script:
init( {damageType}) {
      this.damageType = damageType || "default";
      this.particles = [];

      // Spawn particles based on damage type
      const particleCount = 5;
      for (let i = 0; i < particleCount; i++) {
          this.particles.push({
              x: 0, // Relative to parent
              y: 0,
              vx: (Math.random() - 0.5) * 4, // Velocity (-2 to 2)
              vy: (Math.random() - 0.5) * 4,
              size: Math.random() * 3 + 1, // 1-4 pixels
              alpha: 1, // Fades from 1 to 0
              color: this.getColor()
          });
      }
  }

  getColor() {
      switch (this.damageType) {
          case "electric": return "rgba(0, 255, 255, "; // Cyan for electric
          case "fire": return "rgba(255, 165, 0, "; // Orange for fire
          case "ice": return "rgba(128, 128, 255, "; // Blue for ice
          case "plasma": return "rgba(255, 0, 255, "; // Purple for plasma
          default: return "rgba(255, 255, 255, "; // White fallback
      }
  }

  update() {
      const lifespanComp = this.parent.getComponent("LifeSpan");
      if (!lifespanComp) return; // Safety check

      const fadeFactor = lifespanComp.lifeSpan / 30; // Normalize to initial lifespan (30 frames)
      for (let particle of this.particles) {
          particle.x += particle.vx * this.game.state.timeScale;
          particle.y += particle.vy * this.game.state.timeScale;
          particle.alpha = fadeFactor; // Fade based on remaining lifespan
          particle.size *= 0.98; // Slight shrink per frame
      }
  }

lifeSpan.script:
init( {lifeSpan = 30}) {
    this.lifeSpan = lifeSpan;
}
update() {        
    if( this.lifeSpan > 0 ) {
        this.lifeSpan--;
    } else {
        this.parent.destroy();
    }
}

chainProjectile.script:
init( { spawnType, owner, target, stats }) {        
    this.type = spawnType;
    this.owner = owner;
    this.target = target;
    this.stats = stats;
    this.piercedEnemies = [];
    this.ownerStats = this.owner.getComponent("stats").stats;
    this.chainTargets = []; // Store all targets hit in one frame
    this.hasStruck = false; // Flag to strike only once
}

update() {
    if (this.hasStruck) {
        this.parent.destroy(); // Destroy after one frame of striking
        return;
    }

    if (!this.target || this.target.destroyed) {
        this.parent.destroy();
        return;
    }

    // Strike the initial target
    let targetHealth = this.target.getComponent("health");
    let targetEnergyShield = this.target.getComponent("energyshield");
    let targetStats = {...this.target.getComponent("stats").stats};
    targetStats.energyShield = targetEnergyShield.energyShield;
    let damageResult = engine.getFunction("calculateDamage")(this.stats, targetStats);                    
    if( damageResult.wasEvaded ) { return; }         
    targetHealth.hp -= damageResult.damageDealt;
    targetEnergyShield.absorbDamage(damageResult.damageAbsorbed);
    this.piercedEnemies.push(this.target);
    this.chainTargets.push(this.target);
    this.game.spawn(this.target.position.x, this.target.position.y, "hitEffect", { damageType: this.stats.damageType});
    // Chain to nearby enemies
    if (this.stats.piercing > 0 && this.piercedEnemies.length <= this.stats.piercing) {
        const nearbyEnemies = this.game.spatialGrid.getNearbyEntities(
            this.target.gridPosition.x, 
            this.target.gridPosition.y, 
            this.ownerStats.range
        );

        for (let enemy of nearbyEnemies) {
            if (enemy.destroyed || this.piercedEnemies.includes(enemy)) continue;
            const dx = enemy.position.x - this.target.position.x;
            const dy = enemy.position.y - this.target.position.y;
            const distSq = dx * dx + dy * dy;
            let gridSize = this.game.config.configs.game.gridSize;
            if (distSq <= this.ownerStats.range * this.ownerStats.range * gridSize * gridSize) {
                let enemyHealth = enemy.getComponent("health");
                let enemyEnergyShield = enemy.getComponent("energyshield");
                let enemyStats = {...enemy.getComponent("stats").stats};
                enemyStats.energyShield = targetEnergyShield.energyShield;
                let damageResult = engine.getFunction("calculateDamage")(this.stats, enemyStats); 
                if(!damageResult.wasEvaded) {
                    enemyHealth.hp -= damageResult.damageDealt;
                    enemyEnergyShield.absorbDamage(damageResult.damageAbsorbed);
                    this.piercedEnemies.push(enemy);
                    this.chainTargets.push(enemy);
                    this.game.spawn(enemy.position.x, enemy.position.y, "hitEffect", { damageType: this.stats.damageType});
                    if (this.piercedEnemies.length > this.stats.piercing) break;
                } else {
                    break;
                }
            }
        }
    }

    // Apply additional effects (e.g., slow, leech) to all hit targets
    for (let enemy of this.chainTargets) {
        if (this.ownerStats.slowEffect) {
            enemy.getComponent("stats").addEffect(this.game.config.effects.slow, this.game.effects.slow, this.ownerStats.slowEffect);
        }
        if (this.ownerStats.leech > 0) {
            const healing = this.stats.damage * this.ownerStats.leech * this.game.state.stats.healingMultiplier;
            this.game.state.bloodCoreHP = Math.min(this.game.state.stats.maxBloodCoreHP, this.game.state.bloodCoreHP + healing);
        }
        if (this.ownerStats.thief && this.ownerStats.thief != 0) {
            const stealAmt = this.stats.damage * this.ownerStats.thief * this.game.state.stats.bloodShardMultiplier;
            this.game.state.bloodShards += stealAmt;
        }
    }

    this.hasStruck = true; // Mark as struck, render lightning this frame
}

effect.script:
init( {config, applyFn, amount }) {
    this.id = config.id;
    this.title = config.title;
    this.desc = config.desc;
    this.lifeTime = config.lifeTime;
    this.applyFn = applyFn;
    this.amount = amount;    
}

update() {
    this.lifeTime--;
    if( this.lifeTime <= 0) this.parent.removeComponent(this);
    return true;        
}

apply(s, add, mul) {
    this.applyFn(s, add, mul, this.amount);
}

renderer.script:
init( { objectType, spawnType, setDirection = -1}) {
      this.images = this.game.imageManager.getImages(objectType, spawnType);  
      this.setDirection = setDirection;
      this.currentDirection = 0; // Default direction (will be 0-7 now)
      // Rotated direction zones 45 degrees counter-clockwise
      // Each zone covers a 45-degree arc (Ï€/4 radians)
      this.directionZones = [
          { min: Math.PI/8, max: 3*Math.PI/8 },     // 0: Down-Right (Southeast)
          { min: 3*Math.PI/8, max: 5*Math.PI/8 },   // 1: Down
          { min: 5*Math.PI/8, max: 7*Math.PI/8 },   // 2: Down-Left (Southwest)
          { min: 7*Math.PI/8, max: -7*Math.PI/8 },  // 3: Left (West)
          { min: -7*Math.PI/8, max: -5*Math.PI/8 }, // 4: Up-Left (Northwest)
          { min: -5*Math.PI/8, max: -3*Math.PI/8 }, // 5: Up
          { min: -3*Math.PI/8, max: -Math.PI/8 },   // 6: Up-Right (Northeast)
          { min: -Math.PI/8, max: Math.PI/8 }       // 7: Right (East)
      ];
  }

  draw() {
      if( this.setDirection < 0 ) {
          const dx = this.parent.position.x - this.parent.lastPosition.x; // Change in x
          const dy = this.parent.position.y - this.parent.lastPosition.y; // Change in y

          // Only update direction if there's movement
          if (Math.abs(dx) > 0.001 || Math.abs(dy) > 0.001) {
              // Calculate angle of movement in radians
              const angle = Math.atan2(dy, dx);

              // Determine which of the 8 directional zones the angle falls into
              for (let i = 0; i < this.directionZones.length; i++) {
                  const zone = this.directionZones[i];

                  // Special handling for the West zone which wraps around from PI to -PI
                  if (i === 3) {
                      if (angle >= zone.min || angle <= zone.max) {
                          this.currentDirection = i;
                          break;
                      }
                  } 
                  // Normal zone check
                  else if (angle >= zone.min && angle < zone.max) {
                      this.currentDirection = i;
                      break;
                  }
              }
          }
      } else {
          this.currentDirection = this.setDirection;
      }


      // Draw the image for the current direction
      if (this.images && this.images.length > this.currentDirection) {
          const image = this.images[this.currentDirection];
          if (image) {
              const imgWidth = image.width;
              const imgHeight = image.height;

              const drawX = this.parent.drawPosition.x - imgWidth / 2;
              const drawY = this.parent.drawPosition.y - imgHeight / 2;

              this.game.ctx.drawImage(image, drawX, drawY);
          }
      }
  }

health.script:
init(){
      let statsComp = this.parent.getComponent('stats');
      this.hp = statsComp.stats.hp;

      statsComp.addStat('maxHp', this.hp);
  }
  update() {        
      if (this.hp <= 0){            
          this.parent.destroy();
      }
  }

  draw() {
      let imageSize = this.game.config.configs.game.imageSize;
      let statsComp = this.parent.getComponent('stats');
      const healthPercentage = this.hp / statsComp.stats.maxHp;
      const barWidth = 30;
      this.game.ctx.fillStyle = 'rgba(0, 0, 0, 0.2)';

      const isoPos = this.game.translator.pixelToIso(this.parent.position.x, this.parent.position.y)

      this.game.ctx.fillRect(isoPos.x - barWidth/2, isoPos.y - imageSize * .3, barWidth, 5);
      if( healthPercentage >= 0 ) {
          this.game.ctx.fillStyle = healthPercentage > 0.5 ? 'rgba(0, 255, 0, 0.5)' : healthPercentage > 0.25 ? 'rgba(255, 255, 0, 0.5)' : 'rgba(255, 0, 0, 0.5)';
          this.game.ctx.fillRect(isoPos.x - barWidth/2, isoPos.y - imageSize * .3, barWidth * healthPercentage, 5);
      }
  }

energyShield.script:
init(){
       // Get stats component for reference
       let statsComp = this.parent.getComponent('stats');

       // Shield configuration with defaults
       this.maxEnergyShield = statsComp.stats.energyShield || 0;
       this.energyShield = this.maxEnergyShield || 0;
       this.rechargeRate = statsComp.stats.rechargeRate || 10; // Shield points per second
       this.rechargeDelay = statsComp.stats.rechargeDelay || 2000; // Milliseconds before recharge starts
       this.lastDamageTime = 0; // Timestamp of last damage taken

       // Add relevant stats to the stats component
       statsComp.addStat('maxEnergyShield', this.maxEnergyShield);
       statsComp.addStat('shieldRechargeRate', this.rechargeRate);
       statsComp.addStat('shieldRechargeDelay', this.rechargeDelay);

       // Visual settings
       this.shieldColor = 'rgba(77, 166, 255, 0.6)'; // Light blue default
       this.shieldEmptyColor = 'rgba(77, 166, 255, 0.2)';
       this.barWidth = 30;
       this.barHeight = 5;
       this.barOffset = 8; // Distance above health bar
   }

   /**
    * Handle incoming damage
    * @param {number} damage - Amount of incoming damage
    * @returns {Object} - Damage breakdown
    */
   absorbDamage(damage) {
       const result = {
           absorbedByShield: 0,
           remainingDamage: 0
       };

       // Reset recharge timer when taking damage
       this.lastDamageTime = this.game.currentTime;

       if (this.energyShield > 0) {
           if (damage <= this.energyShield) {
               // Shield absorbs all damage
               result.absorbedByShield = damage;
               this.energyShield -= damage;
           } else {
               // Shield is depleted, remaining damage goes through
               result.absorbedByShield = this.energyShield;
               result.remainingDamage = damage - this.energyShield;
               this.energyShield = 0;
           }
       } else {
           // No shield available
           result.remainingDamage = damage;
       }

       return result;
   }

   /**
    * Update shield state (recharging)
    */
   update() {
       let statsComp = this.parent.getComponent('stats');
       this.maxEnergyShield = statsComp.stats.energyShield || 0;
       // Check if enough time has passed since last damage
       const timeSinceLastDamage = this.game.currentTime - this.lastDamageTime;

       if (timeSinceLastDamage >= this.rechargeDelay && this.energyShield < this.maxEnergyShield) {
           // Calculate recharge amount based on time elapsed and rate
           const deltaTime = this.game.deltaTime || 16; // Fallback to 60fps if deltaTime not available
           const rechargeAmount = (this.rechargeRate * deltaTime) / 1000; // Convert to per-frame amount

           // Apply recharge
           this.energyShield = Math.min(this.maxEnergyShield, this.energyShield + rechargeAmount);
       }
   }

   /**
    * Draw shield bar
    */
   draw() {
       if (this.maxEnergyShield <= 0) return; // Don't draw if no shield capacity

       const shieldPercentage = this.energyShield / this.maxEnergyShield;        

       // Shield bar will be slightly above that
       const barY = this.parent.drawPosition.y - this.game.config.configs.game.imageSize * .3 - this.barOffset;

       // Draw shield background/empty bar
       this.game.ctx.fillStyle = this.shieldEmptyColor;
       this.game.ctx.fillRect(this.parent.drawPosition.x - this.barWidth/2, barY, this.barWidth, this.barHeight);

       // Draw current shield level
       if (shieldPercentage > 0) {
           this.game.ctx.fillStyle = this.shieldColor;
           this.game.ctx.fillRect(
               this.parent.drawPosition.x - this.barWidth/2, 
               barY, 
               this.barWidth * shieldPercentage, 
               this.barHeight
           );
       }

       // Draw recharge indicator if currently recharging
       const timeSinceLastDamage = this.game.currentTime - this.lastDamageTime;
       if (timeSinceLastDamage < this.rechargeDelay && this.energyShield < this.maxEnergyShield) {
           const rechargePercentage = timeSinceLastDamage / this.rechargeDelay;

           // Draw a small indicator below the shield bar
           this.game.ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';
           this.game.ctx.fillRect(
               this.parent.drawPosition.x - this.barWidth/2, 
               barY + this.barHeight + 1, 
               this.barWidth * rechargePercentage, 
               2
           );
       }
   }

   /**
    * Get current shield value
    * @returns {number} Current shield amount
    */
   getShieldValue() {
       return this.energyShield;
   }

   /**
    * Reset shield to full
    */
   resetShield() {
       this.energyShield = this.maxEnergyShield;
   }

   /**
    * Temporarily boost shield capacity
    * @param {number} amount - Amount to boost by
    * @param {number} duration - Duration in milliseconds
    */
   boostShield(amount, duration) {
       const originalMax = this.maxEnergyShield;
       this.maxEnergyShield += amount;
       this.energyShield += amount;

       // Reset after duration
       setTimeout(() => {
           this.maxEnergyShield = originalMax;
           this.energyShield = Math.min(this.energyShield, this.maxEnergyShield);
       }, duration);
   }

rangeIndicator.script:
init() {
    this.ctx = this.game.ctx;
    this.translator = this.game.translator; // Access translator for tileWidth
}

draw() {
    let statsComp = this.getComponent('stats');
    if (!statsComp || !statsComp.stats || !statsComp.stats.range) {
        return;
    }
    this.drawRangeIndicator(statsComp.stats.range);
}

drawRangeIndicator(range) {    
    const drawRage = range - 1;        
    const pixelX = this.parent.position.x;
    const pixelY = this.parent.position.y;
    let gridPos = this.translator.pixelToGrid(pixelX, pixelY);
    gridPos = this.translator.snapToGrid(gridPos.x, gridPos.y);
    const isoPos = this.translator.pixelToIso(pixelX, pixelY);

    const isoRangeX = drawRage * this.game.config.configs.game.gridSize;  // Matches gridToIso X scaling
    const isoRangeY = drawRage * this.game.config.configs.game.gridSize * 0.5; // Matches gridToIso Y scaling

    if (gridPos.x === this.game.state.mousePosition.gridX && gridPos.y === this.game.state.mousePosition.gridY) {
        this.ctx.save();
        this.ctx.beginPath();
        this.ctx.strokeStyle = 'rgba(255, 0, 0, 0.5)';
        this.ctx.lineWidth = 2;

        this.ctx.ellipse(
            isoPos.x,
            isoPos.y,
            isoRangeX,
            isoRangeY,
            0,
            0,
            2 * Math.PI
        );

        this.ctx.stroke();
        this.ctx.closePath();
        this.ctx.restore();
    }    

}

hitEffectRenderer.script:
init( ) {
       this.ctx = this.game.ctx;
   }

   draw() {
       const particleComp = this.parent.getComponent("HitEffectParticle");
       if (!particleComp || !particleComp.particles.length) return;

       const basePos = this.game.translator.pixelToIso(this.parent.position.x, this.parent.position.y);

       for (let particle of particleComp.particles) {
           this.ctx.fillStyle = `${particle.color}${particle.alpha})`;
           this.ctx.beginPath();
           const drawX = basePos.x + particle.x;
           const drawY = basePos.y + particle.y;
           this.ctx.arc(drawX, drawY, particle.size, 0, Math.PI * 2);
           this.ctx.fill();
       }
   }

uiManager.script:
    init() {
        this.canvas = this.game.canvas;
        this.ctx = this.game.ctx;
        this.game.uiManager = this;
        this.upgradeMenu = document.getElementById('upgradeMenu');
        this.upgradeOptionsDiv = document.getElementById('upgradeOptions');
        this.overlay = document.getElementById('overlay');
        this.tooltip = document.getElementById('tooltip');
        this.gameOverMenu = document.getElementById('gameOverMenu');
        this.victoryMenu = document.getElementById('victoryMenu');

        // Stats displays
        this.shardsDisplay = document.getElementById('shardsDisplay');
        this.essenceDisplay = document.getElementById('essenceDisplay');
        this.essenceNeededDisplay = document.getElementById('essenceNeededDisplay');
        this.populationDisplay = document.getElementById('populationDisplay');
        this.maxPopulationDisplay = document.getElementById('maxPopulationDisplay');
        this.hpDisplay = document.getElementById('hpDisplay');
        this.waveDisplay = document.getElementById('waveDisplay');
        this.waveProgress = document.getElementById('waveProgress');
        this.gameOverWave = document.getElementById('gameOverWave');
        this.towerMenu = document.getElementById('towerMenu');
        let towerMenuOptions = '';
        for(let type in this.game.config.towers) {
            if(this.game.config.towers[type].cost > 0){
                towerMenuOptions += `<div class="tower-option" data-type="${type}">${this.game.config.towers[type].title} (${this.game.config.towers[type].cost})</div>`;
            }
        }
        this.towerMenu.innerHTML = towerMenuOptions;
       this.setupTowerPlacement();
       this.setupEventListeners();
    }

  setupEventListeners() {
        document.getElementById('startGameBtn').removeAttribute('style');
        document.getElementById('startGameBtn').addEventListener('click', (e) => {    
            this.game.state.isPaused = false;
            e.target.setAttribute('style','display:none;');
        });
        document.querySelector("#gameOverMenu .menu-button").addEventListener("click", (e) => {
            this.game.reset();
        });

        document.querySelector("#victoryMenu .menu-button").addEventListener("click", (e) => {
            this.game.reset();
        });
this.game.canvas.addEventListener('mousemove', (e) => {
            if (!this.game.state.selectedTowerType && !this.game.state.towers.length) return;

            const rect = this.game.canvas.getBoundingClientRect();
            const mouseX = e.clientX - rect.left;
            const mouseY = e.clientY - rect.top;
            const gridPos = this.game.translator.isoToGrid(mouseX, mouseY);
            const snappedGrid = this.game.translator.snapToGrid(gridPos.x, gridPos.y);
            const isoSnappedGridPos = this.game.translator.gridToIso(snappedGrid.x, snappedGrid.y);
            const pixelIsoPos = this.game.translator.pixelToIso(mouseX, mouseY);


            if (this.game.state.selectedTowerType && this.game.state.previewTower) {
                const snappedPixelPos = this.game.translator.isoToPixel(isoSnappedGridPos.x, isoSnappedGridPos.y);
                this.game.state.previewTower.position.x = this.game.state.mousePosition.gridX * this.game.config.configs.game.gridSize + this.game.config.configs.game.gridSize / 2;
                this.game.state.previewTower.position.y = this.game.state.mousePosition.gridY * this.game.config.configs.game.gridSize + this.game.config.configs.game.gridSize / 2;
                const isValidPosition = this.checkValidTowerPosition(this.game.state.mousePosition.gridX, this.game.state.mousePosition.gridY);
                this.game.canvas.style.cursor = isValidPosition ? 'pointer' : 'not-allowed';
            }

            let hoveredTower = null;
            for (const tower of this.game.state.towers) {
                const towerIso = this.game.translator.pixelToIso(tower.x, tower.y);
                const dist = Math.hypot(towerIso.x - mouseX, towerIso.y + this.game.translator.tileHeight / 2 - mouseY);
                if (dist < 20) {
                    hoveredTower = tower;
                    break;
                }
            }

            if (hoveredTower) {
                let info = `${hoveredTower.type} (Level ${hoveredTower.level})\n`;
                info += `Damage: ${Math.round(hoveredTower.stats.damage * this.game.state.stats.damageMultiplier * 10) / 10}\n`;
                info += `Attack Speed: ${Math.round(1000 / hoveredTower.stats.attackSpeed)} per sec\n`;
                info += `Range: ${hoveredTower.stats.range}\n`;
                info += `Crit Chance: ${Math.round(hoveredTower.stats.critChance * 100)}%\n`;
                if (hoveredTower.stats.leech > 0) {
                    info += `Life Leech: ${Math.round(hoveredTower.stats.leech * 100 * this.game.state.stats.healingMultiplier) / 100} HP per hit\n`;
                }
                if (hoveredTower.stats.piercing > 0) {
                    info += `Piercing: ${hoveredTower.stats.piercing} enemies\n`;
                }
                if (hoveredTower.stats.summonChance > 0) {
                    info += `Summon Chance: ${Math.round(hoveredTower.stats.summonChance * 100)}%\n`;
                }

                this.showTooltip(e.clientX, e.clientY, info);
                hoveredTower.showRange = true;
            } else {
                this.hideTooltip();
                this.game.state.towers.forEach(t => t.showRange = false);
            }
        });

        this.game.canvas.addEventListener('mouseout', () => {
            this.hideTooltip();
        });

        this.game.canvas.addEventListener('click', (e) => {
            if (!this.game.state.selectedTowerType) return;

            const rect = this.game.canvas.getBoundingClientRect();
            const mouseX = e.clientX - rect.left;
            const mouseY = e.clientY - rect.top;

            // Convert mouse (isometric pixel) to grid
            const gridPos = this.game.translator.isoToGrid(mouseX, mouseY);
            const snappedGrid = this.game.translator.snapToGrid(gridPos.x, gridPos.y);

            // Convert snapped grid back to isometric for preview
            const isoSnappedGridPos = this.game.translator.gridToIso(snappedGrid.x, snappedGrid.y); 
            const snappedPixelPos = this.game.translator.isoToPixel(isoSnappedGridPos.x, isoSnappedGridPos.y);

            if (this.checkValidTowerPosition(snappedGrid.x, snappedGrid.y)) {
                // Create the tower
                let cost = this.game.config.towers[this.game.state.selectedTowerType].cost;
                let populationCost = this.game.config.towers[this.game.state.selectedTowerType].population || 0;

                const finalCost = Math.floor(cost * this.game.state.stats.towerCostMod);

                if (this.game.state.bloodShards >= finalCost && this.game.state.stats.population + populationCost <= this.game.state.stats.maxPopulation) {
                    const tower = this.game.spawn(snappedPixelPos.x + this.game.config.configs.game.gridSize / 2, 
                                                 snappedPixelPos.y + this.game.config.configs.game.gridSize / 2, "tower", { objectType: "towers", spawnType: this.game.state.selectedTowerType, setDirection: 1});
                    tower.placed = true;
                    this.game.state.tileMap[snappedGrid.y][snappedGrid.x].buildable = false;
                    this.game.state.tileMap[snappedGrid.y][snappedGrid.x].tower = tower;
                    this.game.state.bloodShards -= finalCost;
                    this.game.state.previewTower.destroy();
                    this.game.state.previewTower = null;
                    // Clear selection
                    this.game.state.selectedTowerType = null;
                    this.game.canvas.style.cursor = 'default';
                }
            }
        });

        // Cancel tower placement with right click
        this.game.canvas.addEventListener('contextmenu', (e) => {
            e.preventDefault();
            if (this.game.state.selectedTowerType) {
                this.game.state.selectedTowerType = null;
                this.game.canvas.style.cursor = 'default';
            }
        });
}

  setupTowerPlacement() {
        let endPath = this.game.state.paths[0][this.game.state.paths[0].length - 1];
        let endY = endPath.y;
        let endX = endPath.x;

        const keep = this.game.spawn(endX * this.game.config.configs.game.gridSize + this.game.config.configs.game.gridSize / 2, 
                                      endY * this.game.config.configs.game.gridSize + this.game.config.configs.game.gridSize / 2, "tower",
                                      { spawnType: 'keep', objectType: 'towers', setDirection: 1});
        keep.placed = true;

        const towerButtons = document.querySelectorAll('.tower-option');
        towerButtons.forEach(button => {
            button.addEventListener('click', () => {

                if(this.game.state.isPaused) return;

                const type = button.getAttribute('data-type');
                let cost = this.game.config.towers[type].cost;
                const finalCost = Math.floor(cost * this.game.state.stats.towerCostMod);

                let populationCost = this.game.config.towers[type].population || 0;
                if (this.game.state.bloodShards >= finalCost && this.game.state.stats.population + populationCost <= this.game.state.stats.maxPopulation) {
                    this.game.state.selectedTowerType = type;
                    if(this.game.state.previewTower) {
                        this.game.state.previewTower.destroy();
                    }
                    this.game.state.previewTower = this.game.spawn(-100, -100, 'previewTower', { objectType: "towers", spawnType: this.game.state.selectedTowerType});
                }
            });

            // Show tooltip with info
            button.addEventListener('mouseover', (e) => {
                const type = button.getAttribute('data-type');
                let info = this.game.config.towers[type].info;

                this.showTooltip(e.clientX, e.clientY, info);
            });

            button.addEventListener('mouseout', () => {
                this.hideTooltip();
            });
        });


    }

    checkValidTowerPosition(posX, posY) {
        if(posX >= 0 && posY >= 0 && this.game.state.tileMap.length > posY && this.game.state.tileMap[posY].length > posX){
            return this.game.state.tileMap[posY][posX].buildable;            
        }
        return false;
    }

    reset() {
        this.gameOverMenu.style.display = 'none';
        this.victoryMenu.style.display = 'none';
        this.overlay.style.display = 'none';
        this.waveDisplay.textContent = '1';
        this.waveProgress.style.width = '0%';        


    }


    draw() {
       this.shardsDisplay.textContent = Math.floor(this.game.state.bloodShards);
        this.essenceDisplay.textContent = Math.floor(this.game.state.essence);
        this.essenceNeededDisplay.textContent = Math.floor(this.game.state.essenceToNextLevel);
        this.hpDisplay.textContent = Math.floor(this.game.state.bloodCoreHP);
        this.populationDisplay.textContent = Math.floor(this.game.state.stats.population);
        this.maxPopulationDisplay.textContent = Math.floor(this.game.state.stats.maxPopulation);  
        if (this.game.state.enemies.length === 0 && this.game.state.enemiesSpawned >= this.game.state.numEnemiesInWave && !this.game.state.victory) {
            const countdown = Math.ceil((this.game.state.waveDelay - this.game.state.waveTimer) / 60);
            this.ctx.fillStyle = 'white';
            this.ctx.font = '20px Arial';
            this.ctx.textAlign = 'center';
            this.ctx.fillText(`Next Wave in ${countdown}...`, this.canvas.width / 2, 50);
        }  
}

    updateWaveDisplay(waveNumber) {
        this.waveDisplay.textContent = waveNumber;
    }


    showTooltip(x, y, text) {
        const tooltip = document.getElementById('tooltip');
        tooltip.style.display = 'block';
        tooltip.style.left = (x + 10) + 'px';
        tooltip.style.top = (y + 10) + 'px';
        tooltip.textContent = text;
    }

    hideTooltip() {
        const tooltip = document.getElementById('tooltip');
        tooltip.style.display = 'none';
    }

lightningRenderer.script:
init( {owner}) {
      this.ctx = this.game.ctx;
      const ownerStats = owner.getComponent("stats").stats;
      this.startOffsetY = ownerStats.projectileStartOffsetY || this.game.configs.game.GRID_SIZE / 2;
  }

  draw() {
      const projectile = this.parent.getComponent("ChainProjectile");
      if (!projectile || !projectile.chainTargets.length) return;

      this.ctx.strokeStyle = "rgba(0, 255, 255, 0.8)"; // Cyan lightning
      this.ctx.lineWidth = 2;
      let stats = this.parent.getComponent('stats').stats;
      // Start from Tesla Coil (parent position)
      let startPos = this.game.translator.pixelToIso(this.parent.position.x, this.parent.position.y);
      startPos.y -= this.startOffsetY;//dont shoot off the ground
      for (let i = 0; i < projectile.chainTargets.length; i++) {
          const target = projectile.chainTargets[i];
          if (!target || target.destroyed) continue;

          const endPos = this.game.translator.pixelToIso(target.position.x, target.position.y);
          this.drawLightning(startPos, endPos);

          // Next arc starts from this target
          startPos = endPos;
      }
  }

  drawLightning(startPos, endPos) {
      this.ctx.beginPath();            
      this.ctx.moveTo(startPos.x, startPos.y);

      const segments = 8; // Number of zigzag points
      const dx = (endPos.x - startPos.x) / segments;
      const dy = (endPos.y - startPos.y) / segments;

      let currentX = startPos.x;
      let currentY = startPos.y;

      for (let i = 1; i < segments; i++) {
          currentX += dx;
          currentY += dy;

          // Add random offset for jagged effect
          const offsetX = (Math.random() - 0.5) * 10; // Max 10px jitter
          const offsetY = (Math.random() - 0.5) * 10;

          this.ctx.lineTo(currentX + offsetX, currentY + offsetY);
      }

      this.ctx.lineTo(endPos.x, endPos.y);
      this.ctx.stroke();
  }

explosion.script:
init( {radius}) {
     this.maxRadius = radius * this.game.config.configs.game.gridSize;
     this.currentRadius = 0;
     this.alpha = 1;
 }

 update() {
     this.currentRadius += this.maxRadius * 0.1;
     this.alpha -= 0.05;
     if( this.alpha < 0 ) {
         this.parent.destroy();
     }
 }

 draw() {
     const pixelX = this.parent.position.x;
     const pixelY = this.parent.position.y;

     // Convert pixel to isometric
     const isoPos = this.game.translator.pixelToIso(pixelX, pixelY);
     const isoRadiusX = this.currentRadius;         // X-axis range remains roughly the same in isometric space
     const isoRadiusY = this.currentRadius * 0.5;   // Y-axis range is halved due to isometric compression

     this.game.ctx.beginPath();
     this.game.ctx.ellipse(
             isoPos.x,           // Center x in isometric coords
             isoPos.y,           // Center y in isometric coords
             isoRadiusX,          // X radius (wider due to isometric projection)
             isoRadiusY,          // Y radius (shorter due to flattening)
             0,                  // Rotation (none needed for isometric)
             0,                  // Start angle
             2 * Math.PI         // End angle (full circle)
         );
     this.game.ctx.fillStyle = `rgba(255, 200, 0, ${this.alpha * 0.5})`;
     this.game.ctx.fill();

     this.game.ctx.beginPath();
     this.game.ctx.ellipse(
             isoPos.x,           // Center x in isometric coords
             isoPos.y,           // Center y in isometric coords
             isoRadiusX * .7,          // X radius (wider due to isometric projection)
             isoRadiusY * .7,          // Y radius (shorter due to flattening)
             0,                  // Rotation (none needed for isometric)
             0,                  // Start angle
             2 * Math.PI         // End angle (full circle)
         );
     this.game.ctx.fillStyle = `rgba(255, 100, 0, ${this.alpha})`;
     this.game.ctx.fill();

 }

calculateStats.script:
function calculateStats(stats, calcArray) {

    if( calcArray && calcArray.length > 0 ) {
        let additiveStats = {};
        let multiplicitiveStats = {};
        for(let key in stats) {
            additiveStats[key] = [];
            multiplicitiveStats[key] = [];
        }
        for(let effect of calcArray) {
            effect.apply(stats, additiveStats, multiplicitiveStats);
        }
        let addedEffects = {};
        for(let key in additiveStats){
            for(let val of additiveStats[key]){ 
                if(addedEffects[key]){
                    addedEffects[key] += val - 1;
                } else {
                    addedEffects[key] = val - 1;
                }
            }
        }

        for(let key in addedEffects) {
            if( stats[key] ) {
                stats[key] *= ( 1 + addedEffects[key] );
            }
        }

        let multipliedUpgrades = {};        
        for(let key in multiplicitiveStats){
            for(let val of multiplicitiveStats[key]){ 
                if(stats[key]){
                    stats[key] *= val;
                }
            }
        }
    }
}

calculateDamage.script:
function calculateDamage(attack, target) {
    // Set default values if not provided

    attack.speed = attack.speed || 5;     
    attack.piercing = attack.piercing || 0,
    attack.splashRadius = attack.splashRadius || 0;
    attack.critChance = attack.critChance || .05;
    attack.critMultiplier = attack.critMultiplier || 2;
    attack.penetration = {
      "fire": attack.firePenetration || 0,
      "electric": attack.electricPenetration || 0,
      "cold": attack.coldPenetration || 0,
      "physical": attack.physicalPenetration || 0,
      "toxic": attack.toxicPenetration || 0,
    }
    target.resistances = {
      "fire": target.fireResistance || 0,
      "electric": target.electricResistance || 0,
      "cold": target.coldResistance || 0,
      "physical": target.physicalResistance || 0,
      "toxic": target.toxicResistance || 0,
    }

    // Initialize result object
    const result = {
      damageBlocked: 0,
      damageAbsorbed: 0,
      damageDealt: 0,
      energyShieldRemaining: target.energyShield || 0,
      wasEvaded: false,
      wasCritical: Math.random() <= attack.critChance,
      hitDetails: {}
    };

    // Check for evasion
    if (target.evasion && target.evasion > 0) {
      const evasionRoll = Math.random() * 100;
      if (evasionRoll < target.evasion) {
        result.wasEvaded = true;
        result.hitDetails.evasion = "Attack evaded";
        return result;
      }
    }

    // Calculate initial damage
    let damage = attack.baseDamage;

    // Apply critical hit
    if (result.wasCritical) {
      damage *= attack.critMultiplier;
      result.hitDetails.critical = `Critical hit (${attack.critMultiplier}x damage)`;
    }

    // Apply resistances based on damage type

    if(attack.damageType != "physical") {
      const resistance = target.resistances[attack.damageType] || 0;
      const penetration = attack.penetration[attack.damageType] || 0;
      const finalResist = resistance - penetration;
      let resistanceMultiplier = 1 - (finalResist / 100);
      damage *= resistanceMultiplier;


      if (finalResist !== 0) {
        if (finalResist > 0) {
          result.hitDetails.resistance = `${attack.damageType} resisted (${finalResist}%)`;
        } else {
          result.hitDetails.resistance = `${attack.damageType} vulnerability (${-finalResist}%)`;
        }
      }  
    }

    // Apply armor (only affects physical damage)
    if (attack.damageType === "physical" && target.armor > 0) {
      let effectiveArmor = target.armor;
      if (attack.penetration.physical > 0 ) {
        // Piercing ignores 75% of armor
        effectiveArmor = target.armor * (1 - attack.penetration.physical);
      }
      // Armor formula: damage reduction percentage = armor / (armor + 100)
      const armorReduction = effectiveArmor / (effectiveArmor + 100);
      const blockedDamage = damage * armorReduction;

      damage -= blockedDamage;
      result.damageBlocked = blockedDamage;
      result.hitDetails.armor = `Armor blocked ${blockedDamage.toFixed(1)} damage`;
    }

    // Round damage to 1 decimal place for cleaner numbers
    damage = Math.round(damage * 10) / 10;

    // Apply shield absorption
    if (target.energyShield > 0) {
      if (damage <= target.energyShield) {
        // Shield absorbs all damage
        result.damageAbsorbed = damage;
        result.energyShieldRemaining = target.energyShield - damage;
        result.hitDetails.energyShield = `Shield absorbed all damage, ${result.energyShieldRemaining.toFixed(1)} shield remaining`;
        damage = 0;
      } else {
        // Shield is depleted, remaining damage goes through
        result.damageAbsorbed = target.energyShield;
        result.damageDealt = damage - target.energyShield;
        result.energyShieldRemaining = 0;
        result.hitDetails.energyShield = `Shield depleted, ${result.damageDealt.toFixed(1)} damage dealt to health`;
        damage -= target.energyShield;
      }
    } else {
      // No shield, all damage goes to health
      result.damageDealt = damage;
    }

    // Make sure we don't return negative damage
    result.damageDealt = Math.max(0, result.damageDealt);

    return result;
  }

